import Vue from 'vue'
import VueRouter from 'vue-router'
import main from '@/views/layout/index'
import index from '@/views/index/index'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'main',
    component: main,
    redirect: '/index',
    children: [
      {
        path: 'index',
        name: 'index',
        component: index,
      }
    ]
  },
]

const router = new VueRouter({
  history: createWebHistory(import.meta.env.BASE_URL),
  routes
})

export default router
