export const mixin = {
    methods:{
        // 上传地址
        uploadUrl(){
            return this.$store.state.configure.HOST + "/common/uploadImage"
        },
        // 文件超出个数限制时的钩子
        handleExceed(files, fileList) {
            this.$message.warning("当前文件数量超出上传限制");
        },
    }
}
