import Vue from 'vue'
import VueRouter from 'vue-router'
import login from '@/views/system/login/login'
import main from '@/views/layout/index'
import index from '@/views/system/index/index'
import type from '@/views/system/type/type'
import car from '@/views/system/car/car'
import order from '@/views/system/order/order'
import returns from '@/views/system/return/return'
import fault from '@/views/system/fault/fault'
import service from '@/views/system/service/service'
import fine from '@/views/system/fine/fine'
import forum from '@/views/system/forum/forum'
import forumItem from '@/views/system/forum/forumItem'
import announcement from '@/views/system/announcement/announcement'
import seek from '@/views/system/seek/seek'
import admin from '@/views/system/user/admin'
import user from '@/views/system/user/user'
import center from '@/views/system/center/center'
import error404 from '@/views/error/404'

Vue.use(VueRouter)

const routes = [
  {
    path: "",
    name: 'main',
    component: main,
    redirect: '/index',
    children: [
      {
        path: 'index',
        name: '首页',
        component: index
      },
      {
        path: 'type',
        name: '汽车类型管理',
        component: type
      },
      {
        path: 'car',
        name: '汽车信息管理',
        component: car
      },
      {
        path: 'order',
        name: '租赁订单管理',
        component: order
      },
      {
        path: 'returns',
        name: '租赁还车管理',
        component: returns
      },
      {
        path: 'admin',
        name: '管理员管理',
        component: admin
      },
      {
        path: 'user',
        name: '普通用户管理',
        component: user
      },
      {
        path: 'fault',
        name: '故障申报管理',
        component: fault
      },
      {
        path: 'service',
        name: '车辆维修管理',
        component: service
      },
      {
        path: 'fine',
        name: '罚款信息管理',
        component: fine
      },
      {
        path: 'forum',
        name: '论坛管理',
        component: forum
      },
      {
        path: 'forumItem',
        name: '论坛评论',
        component: forumItem
      },
      {
        path: 'announcement',
        name: '公告管理',
        component: announcement
      },
      {
        path: 'seek',
        name: '咨询管理',
        component: seek
      },
      {
        path: 'center',
        name: '个人中心',
        component: center
      }
    ]
  },
  {
    path: '/login',
    name: '登陆',
    component: login,
  },
  {
    path: '/404',
    name: '404',
    component: error404,
  }
]

// 防止连续点击多次路由报错
let routerPush = VueRouter.prototype.push;
let routerReplace = VueRouter.prototype.replace;
// push
VueRouter.prototype.push = function push(location) {
  return routerPush.call(this, location).catch(err => err)
}
// replace
VueRouter.prototype.replace = function push(location) {
  return routerReplace.call(this, location).catch(err => err)
}

const router = new VueRouter({
  mode: 'history',
  routes,
})

export default router
