import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    activeMenuArrary: [
      {
        "name": "扶뒬못",
        "url": "/index"
      }
    ],
    activeMenu: "/index",
    keepAlivePage: ""
  },
  getters: {

  },
  mutations: {
    setActiveMenu(state,menu) {
      state.activeMenu = menu
    },
    addActiveMenu(state,menu) {
      var page = []
      state.activeMenuArrary.push(menu)
      for(let i = 0;i < state.activeMenuArrary.length;i++) {
        page.push(state.activeMenuArrary.name)
      }
      state.keepAlivePage = page.join(",")
    },
    reduceActiveMenu(state,i) {
      state.activeMenuArrary.splice(i,1)
    },

  },
  actions: {

  },
  modules: {

  }
})
