const user = {
    state: {
        token: "",
    },
    getters: {
        getToken(state) {
          if (state.token) {
            return state.token
          } else {
            return window.localStorage.getItem("token")
          }
        }
    },
  
    mutations: {
        setToken(state,token) {
            state.token = token
            window.localStorage.setItem("token",token)
        },
    },
  
    actions: {
      
    }
  }
  
export default user
