import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    activeMenu: [
      {
        "name": "扶뒬못",
        "url": "/index"
      }
    ]
  },
  getters: {
    getActiveMenu(activeMenu) {
      return activeMenu;
    }
  },
  mutations: {
    addActiveMenu(activeMenu,menu) {
      activeMenu.push(menu)
    },
    reduceActiveMenu(activeMenu,menu) {
      var index = activeMenu.length-1;
      for(let i = 0;i < activeMenu.length;i++) {
        if(activeMenu[i].url == menu.url) {
          index = i;
          break;
        }
      }
      activeMenu.splice(i,1)
    }
  },
  actions: {

  },
  modules: {

  }
})
