import Vue from 'vue'
import VueRouter from 'vue-router'
import main from '@/views/layout/index'
import index from '@/views/index/index'
import login from '@/views/login/login'
import user from '@/views/system/user/user'
import role from '@/views/system/role/role'
import menu from '@/views/system/menu/menu'
import dept from '@/views/system/dept/dept'
import post from '@/views/system/post/post'

Vue.use(VueRouter)

const routes = [
  {
    path: '/login',
    name: '登陆',
    component: login,
  },
  {
    path: '',
    name: 'main',
    component: main,
    redirect: '/index',
    children: [
      {
        path: '/index',
        name: '首页',
        component: index,
      },
      {
        path: '/user',
        name: '用户管理',
        component: user,
      },
      {
        path: '/role',
        name: '角色管理',
        component: role,
      },
      {
        path: '/menu',
        name: '菜单管理',
        component: menu,
      },
      {
        path: '/dept',
        name: '部门管理',
        component: dept,
      }
      ,
      {
        path: '/post',
        name: '岗位管理',
        component: post,
      }
    ]
  },
]

const router = new VueRouter({
  mode: 'history',
  routes
})

export default router
