import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    activeMenuArrary: [
      {
        "name": "扶뒬못",
        "url": "/index"
      }

    ],
    activeMenu: "/index"

  },
  getters: {
    getActiveMenuArrary(activeMenuArrary) {
      return activeMenuArrary;
    }
  },
  mutations: {
    addActiveMenu(activeMenuArrary,menu) {
      activeMenuArrary.push(menu)
    },
    reduceActiveMenu(activeMenuArrary,menu) {
      var index = activeMenuArrary.length-1;
      for(let i = 0;i < activeMenuArrary.length;i++) {
        if(activeMenu[i].url == menu) {
          index = i;
          break;
        }
      }
      activeMenuArrary.splice(i,1)
    }
  },
  actions: {

  },
  modules: {

  }
})
